// Admin.js (Full Features)
import React, { useEffect, useState } from 'react';
import { db, storage, auth } from './firebase';
import {
  collection,
  addDoc,
  getDocs,
  deleteDoc,
  updateDoc,
  doc,
  serverTimestamp
} from 'firebase/firestore';
import {
  ref,
  uploadBytes,
  getDownloadURL
} from 'firebase/storage';
import { signInWithEmailAndPassword } from 'firebase/auth';
import Papa from 'papaparse';

const Admin = ({ user }) => {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [category, setCategory] = useState('');
  const [image, setImage] = useState(null);
  const [message, setMessage] = useState('');

  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [showOrders, setShowOrders] = useState(false);
  const [editingProductId, setEditingProductId] = useState(null);

  const [featured, setFeatured] = useState(false);

  const fetchProducts = async () => {
    const snapshot = await getDocs(collection(db, 'products'));
    const list = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    setProducts(list);
  };

  const fetchOrders = async () => {
    const snapshot = await getDocs(collection(db, 'orders'));
    const orderList = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    setOrders(orderList);
    setShowOrders(true);
  };

  const deleteProduct = async (id) => {
    await deleteDoc(doc(db, 'products', id));
    fetchProducts();
  };

  const deleteOrder = async (id) => {
    await deleteDoc(doc(db, 'orders', id));
    fetchOrders();
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!image) return alert("Please select an image.");

    const imageRef = ref(storage, `products/${image.name}`);
    const snapshot = await uploadBytes(imageRef, image);
    const imageUrl = await getDownloadURL(snapshot.ref);

    await addDoc(collection(db, "products"), {
      name,
      price: parseFloat(price),
      category,
      image: imageUrl,
      createdAt: serverTimestamp()
    });

    setName(''); setPrice(''); setCategory(''); setImage(null);
    setMessage("Product uploaded.");
    fetchProducts();
  };

  const startEdit = (product) => {
    setEditingProductId(product.id);
    setName(product.name);
    setPrice(product.price);
    setCategory(product.category);
  };

  const saveEdit = async () => {
    await updateDoc(doc(db, 'products', editingProductId), {
      name,
      price: parseFloat(price),
      category
    });
    setEditingProductId(null);
    setName(''); setPrice(''); setCategory('');
    fetchProducts();
  };

  const exportOrders = () => {
    const csv = Papa.unparse(orders.map(order => ({
      Name: order.customer.name,
      Phone: order.customer.phone,
      Address: order.customer.address,
      Date: order.createdAt?.toDate().toLocaleString(),
      Items: order.items.map(i => `${i.name} x${i.quantity}`).join(', ')
    })));
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'orders.csv';
    a.click();
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <div style={{ padding: '2rem', background: '#1a1a1a', minHeight: '100vh', color: '#fff' }}>
      <h2>Admin Panel</h2>

      {editingProductId ? (
        <>
          <h3>Edit Product</h3>
          <input value={name} onChange={e => setName(e.target.value)} placeholder="Name" />
          <input value={price} onChange={e => setPrice(e.target.value)} placeholder="Price" type="number" />
          <input value={category} onChange={e => setCategory(e.target.value)} placeholder="Category" />
          <button onClick={saveEdit}>Save Changes</button>
        </>
      ) : (
        <>
          <h3>Add New Product</h3>
          <form onSubmit={handleUpload} style={{ display: 'flex', flexDirection: 'column', maxWidth: '400px' }}>
            <input value={name} onChange={(e) => setName(e.target.value)} placeholder="Name" required />
            <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} placeholder="Price" required />
            <input value={category} onChange={(e) => setCategory(e.target.value)} placeholder="Category" required />
            <input type="file" onChange={(e) => setImage(e.target.files[0])} required />
            <label style={{ color: '#fff', marginBottom: '1rem' }}>
    <input
      type="checkbox"
      checked={featured}
      onChange={(e) => setFeatured(e.target.checked)}
      style={{ marginRight: '0.5rem' }}
    />
    Mark as Featured
  </label>

            <button type="submit">Upload</button>
          </form>
        </>
      )}

      {message && <p style={{ color: 'lightgreen' }}>{message}</p>}

      <hr />
      <h3>Products</h3>
      {products.map(p => (
        <div key={p.id} style={{ background: '#333', padding: '1rem', marginBottom: '1rem' }}>
          <strong>{p.name}</strong> - ${p.price} ({p.category})<br />
          <img src={p.image} alt={p.name} style={{ width: '100px', margin: '0.5rem 0' }} /><br />
          <button onClick={() => startEdit(p)}>Edit</button>
          <button onClick={() => deleteProduct(p.id)} style={{ marginLeft: '1rem', background: 'crimson', color: 'white' }}>Delete</button>
        </div>
      ))}

      <hr />
      <h3>Orders</h3>
      <button onClick={fetchOrders}>View Orders</button>
      <button onClick={exportOrders} style={{ marginLeft: '1rem' }}>Export Orders</button>

      {showOrders && orders.map(order => (
        <div key={order.id} style={{ marginTop: '1rem', background: '#333', padding: '1rem' }}>
          <strong>{order.customer.name}</strong><br />
          {order.customer.phone} | {order.customer.address}<br />
          <em>{order.createdAt?.toDate().toLocaleString()}</em>
          <ul>
            {order.items.map((item, i) => (
              <li key={i}>{item.name} x {item.quantity} - ${item.price}</li>
            ))}
          </ul>
          <button onClick={() => deleteOrder(order.id)} style={{ background: 'crimson', color: 'white' }}>Delete</button>
        </div>
      ))}
    </div>
  );
};

export default Admin;
