import React, { useState, useEffect } from 'react';
import { db, storage } from './firebase';
import { collection, addDoc, serverTimestamp, getDocs, deleteDoc, doc, updateDoc } from 'firebase/firestore';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import Papa from 'papaparse';

const Admin = () => {
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [showSection, setShowSection] = useState('products');
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [category, setCategory] = useState('');
  const [image, setImage] = useState(null);
  const [inStock, setInStock] = useState(true);
  const [featured, setFeatured] = useState(false);
  const [message, setMessage] = useState('');
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchProducts();
    fetchOrders();
  }, []);

  const fetchProducts = async () => {
    const querySnapshot = await getDocs(collection(db, 'products'));
    const items = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    setProducts(items);
  };

  const fetchOrders = async () => {
    const querySnapshot = await getDocs(collection(db, 'orders'));
    const items = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    const sortedItems = items.sort((a, b) => b.createdAt?.seconds - a.createdAt?.seconds);
    setOrders(sortedItems);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    try {
      let imageUrl = '';
      if (image) {
        const imageRef = ref(storage, `products/${image.name}`);
        await uploadBytes(imageRef, image);
        imageUrl = await getDownloadURL(imageRef);
      } else if (editId) {
        const existing = products.find(p => p.id === editId);
        imageUrl = existing?.image || '';
      }

      const productData = {
        name,
        price: parseFloat(price),
        category,
        image: imageUrl,
        inStock,
        featured,
        createdAt: serverTimestamp()
      };

      if (editId) {
        await updateDoc(doc(db, 'products', editId), productData);
        setMessage('Product updated successfully!');
        setEditId(null);
      } else {
        await addDoc(collection(db, 'products'), productData);
        setMessage('Product uploaded successfully!');
      }

      setName('');
      setPrice('');
      setCategory('');
      setImage(null);
      setInStock(true);
      setFeatured(false);
      fetchProducts();
    } catch (error) {
      console.error('Upload failed:', error);
      alert('Something went wrong.');
    }
  };

  const handleEdit = (product) => {
    setEditId(product.id);
    setName(product.name);
    setPrice(product.price);
    setCategory(product.category);
    setInStock(product.inStock);
    setFeatured(product.featured);
    setShowSection('add');
  };

  const handleDelete = async (id) => {
    await deleteDoc(doc(db, 'products', id));
    fetchProducts();
  };

  const toggleProductField = async (id, field, value) => {
    await updateDoc(doc(db, 'products', id), { [field]: value });
    fetchProducts();
  };

  const handleDeleteOrder = async (id) => {
    await deleteDoc(doc(db, 'orders', id));
    fetchOrders();
  };

  return (
    <div style={{ padding: '2rem', background: '#1a1a1a', minHeight: '100vh', color: '#fff' }}>
      <h2>Admin Panel</h2>

      <div style={{ marginBottom: '2rem' }}>
        <button onClick={() => setShowSection('add')} style={{ marginRight: '1rem' }}>Add Product</button>
        <button onClick={() => setShowSection('products')} style={{ marginRight: '1rem' }}>View Products</button>
        <button onClick={() => setShowSection('orders')}>View Orders</button>
      </div>

      {showSection === 'add' && (
        <>
          <h3>{editId ? 'Edit Product' : 'Add New Product'}</h3>
          <form onSubmit={handleUpload} style={{ display: 'flex', flexDirection: 'column', maxWidth: '400px' }}>
            <input type="text" placeholder="Product Name" value={name} onChange={(e) => setName(e.target.value)} required style={{ marginBottom: '1rem' }} />
            <input type="number" placeholder="Price" value={price} onChange={(e) => setPrice(e.target.value)} required style={{ marginBottom: '1rem' }} />
            <input type="text" placeholder="Category" value={category} onChange={(e) => setCategory(e.target.value)} required style={{ marginBottom: '1rem' }} />
            <input type="file" onChange={(e) => setImage(e.target.files[0])} style={{ marginBottom: '1rem' }} />
            <label style={{ marginBottom: '0.5rem' }}>
              <input type="checkbox" checked={inStock} onChange={() => setInStock(!inStock)} /> In Stock
            </label>
            <label style={{ marginBottom: '1rem' }}>
              <input type="checkbox" checked={featured} onChange={() => setFeatured(!featured)} /> Mark as Hot Item
            </label>
            <button type="submit">{editId ? 'Update' : 'Upload'} Product</button>
          </form>
          {message && <p style={{ marginTop: '1rem', color: 'lightgreen' }}>{message}</p>}
        </>
      )}

      {showSection === 'products' && (
        <>
          <h3>All Products</h3>
          <ul style={{ paddingLeft: 0, listStyle: 'none' }}>
            {products.map(p => (
              <li key={p.id} style={{ marginBottom: '1rem', background: '#333', padding: '1rem', borderRadius: '8px', display: 'flex', alignItems: 'center' }}>
                {p.image && <img src={p.image} alt={p.name} style={{ width: '60px', height: '60px', objectFit: 'contain', marginRight: '1rem' }} />}
                <div style={{ flexGrow: 1 }}>
                  <strong>{p.name}</strong> — ${p.price.toFixed(2)} — {p.category} — {p.inStock ? 'In Stock' : 'Sold Out'} {p.featured && '(Hot Item)'}
                  <div style={{ marginTop: '0.5rem' }}>
                    <label style={{ marginRight: '1rem' }}>
                      <input
                        type="checkbox"
                        checked={p.featured}
                        onChange={() => toggleProductField(p.id, 'featured', !p.featured)}
                      />{' '}
                      Hot Item
                    </label>
                    <label style={{ marginRight: '1rem' }}>
                      <input
                        type="checkbox"
                        checked={p.inStock}
                        onChange={() => toggleProductField(p.id, 'inStock', !p.inStock)}
                      />{' '}
                      In Stock
                    </label>
                    <button onClick={() => handleEdit(p)} style={{ marginRight: '1rem' }}>Edit</button>
                    <button onClick={() => handleDelete(p.id)} style={{ background: 'crimson', color: '#fff' }}>Delete</button>
                  </div>
                </div>
              </li>
            ))}
          </ul>
        </>
      )}

      {showSection === 'orders' && (
        <div style={{ marginTop: '1rem' }}>
          <h3>Customer Orders</h3>
          {orders.map(order => (
            <div key={order.id} style={{ background: '#444', padding: '1rem', marginBottom: '1rem', borderRadius: '8px' }}>
              <p><strong>Store Name:</strong> {order.name}</p>
              <p><strong>Store Owner:</strong> {order.phone}</p>
              <p><strong>Address:</strong> {order.address}</p>
              <p><strong>Total:</strong> ${order.total.toFixed(2)}</p>
              <ul>
                {order.items.map((item, idx) => (
                  <li key={idx} style={{ display: 'flex', alignItems: 'center', marginBottom: '0.5rem' }}>
                    {item.image && <img src={item.image} alt={item.name} style={{ width: '40px', height: '40px', objectFit: 'contain', marginRight: '0.5rem' }} />}
                    {item.name} × {item.quantity}
                  </li>
                ))}
              </ul>
              <button onClick={() => handleDeleteOrder(order.id)} style={{ marginTop: '0.5rem', background: 'crimson', color: '#fff' }}>Delete Order</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};



export default Admin;
