// App.js (Full Features Updated)
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import { db, auth } from './firebase';
import {
  collection,
  getDocs,
  addDoc,
  serverTimestamp
} from 'firebase/firestore';
import { onAuthStateChanged, signOut } from 'firebase/auth';
import Admin from './Admin';
import { useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { useEffect, useState, useRef } from 'react';
import ProtectedRoute from './ProtectedRoute';
import { FaShoppingCart } from 'react-icons/fa';

const Home = ({ addToCart }) => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [quantities, setQuantities] = useState({});
  const scrollRef = useRef();

  useEffect(() => {
    const fetchProducts = async () => {
      const querySnapshot = await getDocs(collection(db, 'products'));
      const items = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setProducts(items);
      const uniqueCategories = [...new Set(items.map(p => p.category))];
      setCategories(uniqueCategories);
    };

    fetchProducts();
  }, []);

  const filteredProducts = selectedCategory
    ? products.filter(p => p.category === selectedCategory)
    : products;

  const buttonStyle = (active) => ({
    padding: '0.5rem 1.2rem',
    background: active ? '#ff4081' : 'transparent',
    color: active ? '#fff' : '#ff4081',
    border: '2px solid #ff4081',
    borderRadius: '9999px',
    fontWeight: 'bold',
    cursor: 'pointer',
    transition: 'all 0.2s ease-in-out',
    whiteSpace: 'nowrap'
  });

  const handleQuantityChange = (id, qty) => {
    setQuantities({ ...quantities, [id]: qty });
  };

  return (
    <div style={{ padding: '2rem', background: '#1a1a1a', minHeight: '100vh', color: '#fff' }}>
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', position: 'sticky', top: 0, background: '#1a1a1a', zIndex: 10, paddingBottom: '1rem' }}>
        <h1 style={{ textAlign: 'center', marginBottom: '1rem' }}>JM Distribution</h1>
        <div
          ref={scrollRef}
          style={{ display: 'flex', overflowX: 'auto', gap: '0.75rem', marginBottom: '1rem', paddingBottom: '0.5rem', scrollbarWidth: 'none', msOverflowStyle: 'none' }}
        >
          <button onClick={() => setSelectedCategory('')} style={buttonStyle(selectedCategory === '')}>All</button>
          {categories.map(cat => (
            <button key={cat} onClick={() => setSelectedCategory(cat)} style={buttonStyle(selectedCategory === cat)}>{cat}</button>
          ))}
        </div>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '1.5rem', justifyItems: 'center', width: '100%', maxWidth: '1000px', margin: '0 auto' }}>
        <AnimatePresence>
          {filteredProducts.map(p => (
            <motion.div
              key={p.id}
              layout
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0 }}
              transition={{ duration: 0.3 }}
              style={{ position: 'relative', background: '#222', padding: '1rem', borderRadius: '10px', textAlign: 'center', width: '100%', maxWidth: '250px' }}
            >
              {p.featured && (
                <span style={{ position: 'absolute', top: '10px', right: '10px', background: '#ff4081', color: '#fff', padding: '2px 6px', fontSize: '0.75rem', borderRadius: '4px' }}>
                  Hot Item
                </span>
              )}
              {!p.inStock && (
                <span style={{ position: 'absolute', top: '10px', left: '10px', background: 'crimson', color: '#fff', padding: '2px 6px', fontSize: '0.75rem', borderRadius: '4px' }}>
                  Sold Out
                </span>
              )}
              <img src={p.image} alt={p.name} style={{ width: '100%', height: '150px', objectFit: 'contain', marginBottom: '1rem' }} />
              <h3>{p.name}</h3>
              <p>${p.price.toFixed(2)}</p>
              {p.inStock && (
                <>
                  <input
                    type="number"
                    min="1"
                    value={quantities[p.id] || 1}
                    onChange={(e) => handleQuantityChange(p.id, parseInt(e.target.value) || 1)}
                    style={{ width: '60px', marginBottom: '0.5rem' }}
                  />
                  <br />
                  <button onClick={() => addToCart(p, quantities[p.id] || 1)}>Add to Cart</button>
                </>
              )}
            </motion.div>
          ))}
        </AnimatePresence>
      </div>
    </div>
  );
};

const Cart = ({ cart, removeFromCart, updateCartQuantity }) => {
  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
  return (
    <div style={{ padding: '2rem' }}>
      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          <ul>
            {cart.map(item => (
              <li key={item.id} style={{ marginBottom: '1rem' }}>
                <strong>{item.name}</strong> — ${item.price.toFixed(2)} ×
                <input
                  type="number"
                  min="1"
                  value={item.quantity}
                  onChange={(e) => updateCartQuantity(item.id, parseInt(e.target.value) || 1)}
                  style={{ width: '60px', marginLeft: '0.5rem' }}
                />
                <button style={{ marginLeft: '1rem' }} onClick={() => removeFromCart(item.id)}>Remove</button>
              </li>
            ))}
          </ul>
          <h3>Total: ${total.toFixed(2)}</h3>
          <Link to="/checkout">
            <button style={{ marginTop: '1rem' }}>Proceed to Checkout</button>
          </Link>
        </>
      )}
    </div>
  );
};

function App() {
  const [cart, setCart] = useState([]);

  const addToCart = (product, quantity = 1) => {
    const exists = cart.find(item => item.id === product.id);
    if (exists) {
      setCart(cart.map(item =>
        item.id === product.id
          ? { ...item, quantity: item.quantity + quantity }
          : item
      ));
    } else {
      setCart([...cart, { ...product, quantity }]);
    }
    alert(`${quantity} ${product.name}(s) added to cart!`);
  };

  const updateCartQuantity = (id, quantity) => {
    setCart(cart.map(item =>
      item.id === id ? { ...item, quantity } : item
    ));
  };

  const removeFromCart = (id) => {
    setCart(cart.filter(item => item.id !== id));
  };

  const clearCart = () => {
    setCart([]);
  };

  return (
    <Router>
      <nav style={{ padding: '1rem', background: '#000', position: 'sticky', top: 0, zIndex: 20, display: 'flex', justifyContent: 'space-between' }}>
        <div>
          <Link to="/" style={{ color: '#fff', marginRight: '1rem' }}>Home</Link>
        </div>
        <div>
          <Link to="/cart" style={{ color: '#fff', marginRight: '1rem' }}>
            <FaShoppingCart style={{ marginRight: '0.25rem' }} /> Cart
          </Link>
        </div>
      </nav>

      <Routes>
        <Route path="/" element={<Home addToCart={addToCart} />} />
        <Route path="/cart" element={<Cart cart={cart} removeFromCart={removeFromCart} updateCartQuantity={updateCartQuantity} />} />
        <Route path="/checkout" element={<div>Checkout Component</div>} />
      </Routes>
    </Router>
  );
}

export default App;
