import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { collection, getDocs, addDoc, serverTimestamp } from 'firebase/firestore';
import { db } from './firebase';

const sampleProducts = [
  { id: 1, name: 'Wireless Earbuds', price: 29.99 },
  { id: 2, name: 'Smartwatch', price: 49.99 },
  { id: 3, name: 'Bluetooth Speaker', price: 39.99 },
  { id: 4, name: 'LED Desk Lamp', price: 19.99 }
];

const Home = ({ addToCart }) => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      const querySnapshot = await getDocs(collection(db, 'products'));
      const items = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setProducts(items);
    };

    fetchProducts();
  }, []);

  return (
    <div style={{ padding: '2rem' }}>
      <h1>JM Distribution</h1>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1rem' }}>
        {products.map(p => (
          <div key={p.id} style={{ background: '#222', padding: '1rem', borderRadius: '8px' }}>
            <img src={p.image} alt={p.name} style={{ width: '100%', height: '150px', objectFit: 'cover', marginBottom: '1rem' }} />
            <h3>{p.name}</h3>
            <p>${p.price.toFixed(2)}</p>
            <button onClick={() => addToCart(p)}>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
};


const Cart = ({ cart, removeFromCart }) => {
  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          <ul>
            {cart.map(item => (
              <li key={item.id} style={{ marginBottom: '1rem' }}>
                <strong>{item.name}</strong> — ${item.price.toFixed(2)} × {item.quantity}
                <button style={{ marginLeft: '1rem' }} onClick={() => removeFromCart(item.id)}>Remove</button>
              </li>
            ))}
          </ul>
          <h3>Total: ${total.toFixed(2)}</h3>
<Link to="/checkout">
  <button style={{ marginTop: '1rem' }}>Proceed to Checkout</button>
</Link>

        </>
      )}
    </div>
  );
};


//import { addDoc, collection, serverTimestamp } from "firebase/firestore";

const Checkout = ({ cart, clearCart }) => {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [address, setAddress] = useState('');
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (cart.length === 0) {
      alert("Cart is empty.");
      return;
    }

    const order = {
      customer: { name, phone, address },
      items: cart,
      createdAt: serverTimestamp()
    };

    try {
      await addDoc(collection(db, "orders"), order);
      setSubmitted(true);
      clearCart();
    } catch (error) {
      console.error("Error submitting order:", error);
      alert("There was a problem submitting the order.");
    }
  };

  if (submitted) {
    return (
      <div style={{ padding: '2rem' }}>
        <h2>Order Placed!</h2>
        <p>Thank you for your order. We'll contact you shortly.</p>
      </div>
    );
  }

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Checkout</h2>
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', maxWidth: '400px' }}>
        <input placeholder="Full Name" value={name} onChange={e => setName(e.target.value)} required style={{ marginBottom: '1rem' }} />
        <input placeholder="Phone Number" value={phone} onChange={e => setPhone(e.target.value)} required style={{ marginBottom: '1rem' }} />
        <textarea placeholder="Shipping Address" value={address} onChange={e => setAddress(e.target.value)} required style={{ marginBottom: '1rem' }} />
        <button type="submit">Place Order</button>
      </form>
    </div>
  );
};



const Admin = () => (
  <div style={{ padding: '2rem', background: '#1a1a1a', height: '100vh' }}>
    <h1>Admin Dashboard</h1>
    <p>Login, manage products, view orders.</p>
  </div>
);

function App() {
  const [cart, setCart] = useState([]);


  const clearCart = () => {
    setCart([]);
  };



  const addToCart = (product) => {
    const exists = cart.find(item => item.id === product.id);
    if (exists) {
      setCart(cart.map(item =>
        item.id === product.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      ));
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  const removeFromCart = (id) => {
    setCart(cart.filter(item => item.id !== id));
  };

  return (
    <Router>
      <nav style={{ padding: '1rem', background: '#000' }}>
        <Link to="/" style={{ color: '#fff', marginRight: '1rem' }}>Home</Link>
        <Link to="/cart" style={{ color: '#fff', marginRight: '1rem' }}>Cart</Link>
        <Link to="/admin" style={{ color: '#fff' }}>Admin</Link>
        <Link to="/checkout" style={{ color: '#fff', marginRight: '1rem' }}>Checkout</Link>

      </nav>
      <Routes>
        <Route path="/" element={<Home addToCart={addToCart} />} />
        <Route path="/cart" element={<Cart cart={cart} removeFromCart={removeFromCart} />} />
        <Route path="/admin" element={<Admin />} />

      </Routes>
    </Router>
  );
}


export default App;
