import React, { useState, useEffect } from 'react';
import { db, storage } from './firebase';
import { collection, addDoc, serverTimestamp, getDocs, deleteDoc, doc } from 'firebase/firestore';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';

const Admin = () => {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [category, setCategory] = useState('');
  const [image, setImage] = useState(null);
  const [message, setMessage] = useState('');
  const [orders, setOrders] = useState([]);
  const [showOrders, setShowOrders] = useState(false);

  const handleUpload = async (e) => {
    e.preventDefault();
    try {
      let imageUrl = '';
      if (image) {
        const imageRef = ref(storage, `products/${image.name}`);
        await uploadBytes(imageRef, image);
        imageUrl = await getDownloadURL(imageRef);
      }

      await addDoc(collection(db, "products"), {
        name,
        price: parseFloat(price),
        category,
        image: imageUrl,
        inStock: true,
        createdAt: serverTimestamp()
      });

      setMessage("Product uploaded successfully!");
      setName('');
      setPrice('');
      setCategory('');
      setImage(null);
    } catch (error) {
      console.error("Upload failed:", error);
      alert("Something went wrong.");
    }
  };

  const fetchOrders = async () => {
    try {
      const snapshot = await getDocs(collection(db, 'orders'));
      const fetchedOrders = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setOrders(fetchedOrders);
    } catch (error) {
      console.error("Failed to fetch orders:", error);
    }
  };

  const handleDeleteOrder = async (orderId) => {
    try {
      await deleteDoc(doc(db, 'orders', orderId));
      setOrders(prev => prev.filter(order => order.id !== orderId));
    } catch (error) {
      console.error("Failed to delete order:", error);
    }
  };

  return (
    <div style={{ padding: '2rem', background: '#1a1a1a', minHeight: '100vh', color: '#fff' }}>
      <h2>Admin - Add Product</h2>
      <form onSubmit={handleUpload} style={{ display: 'flex', flexDirection: 'column', maxWidth: '400px' }}>
        <input type="text" placeholder="Product Name" value={name} onChange={(e) => setName(e.target.value)} required style={{ marginBottom: '1rem' }} />
        <input type="number" placeholder="Price" value={price} onChange={(e) => setPrice(e.target.value)} required style={{ marginBottom: '1rem' }} />
        <input type="text" placeholder="Category" value={category} onChange={(e) => setCategory(e.target.value)} required style={{ marginBottom: '1rem' }} />
        <input type="file" onChange={(e) => setImage(e.target.files[0])} style={{ marginBottom: '1rem' }} />
        <button type="submit">Upload Product</button>
      </form>
      {message && <p style={{ marginTop: '1rem', color: 'lightgreen' }}>{message}</p>}

      <hr style={{ margin: '2rem 0', borderColor: '#444' }} />
      <button onClick={() => { setShowOrders(!showOrders); if (!showOrders) fetchOrders(); }} style={{ marginBottom: '1rem' }}>
        {showOrders ? 'Hide Orders' : 'Show Orders'}
      </button>

      {showOrders && orders.length > 0 && (
        <div>
          <h3>Customer Orders</h3>
          {orders.map((order, index) => (
            <div key={order.id || index} style={{ marginBottom: '1rem', padding: '1rem', background: '#2a2a2a', borderRadius: '8px' }}>
              <h4 style={{ marginBottom: '0.5rem' }}>JM Distribution</h4>
              <p><strong>Store Owner:</strong> JM Distribution Admin</p>
              <p><strong>Name:</strong> {order.name || 'N/A'}</p>
              <p><strong>Phone:</strong> {order.phone || 'N/A'}</p>
              <p><strong>Address:</strong> {order.address || 'N/A'}</p>
              <p><strong>Total:</strong> ${order.total ? order.total.toFixed(2) : '0.00'}</p>
              <p><strong>Items Ordered:</strong></p>
              <ul>
                {order.items?.map((item, i) => (
                  <li key={i}>{item.name} Ã— {item.quantity}</li>
                ))}
              </ul>
              <button onClick={() => handleDeleteOrder(order.id)} style={{ marginTop: '0.5rem', background: 'crimson', color: '#fff', border: 'none', padding: '0.5rem 1rem', borderRadius: '4px' }}>Delete Order</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Admin;
