          }}
          onTouchMove={(e) => {
            const deltaX = scrollRef.current.touchStartX - e.touches[0].clientX;
            scrollRef.current.scrollLeft += deltaX;
            scrollRef.current.touchStartX = e.touches[0].clientX;
          }}
        >
          <button
            onClick={() => setSelectedCategory('')}
            style={{ padding: '0.5rem 1rem', background: selectedCategory === '' ? '#444' : '#222', color: '#fff', borderRadius: '6px', whiteSpace: 'nowrap' }}
          >
            All
          </button>
          {categories.map(cat => (
            <button
              key={cat}
              onClick={() => setSelectedCategory(cat)}
              style={{ padding: '0.5rem 1rem', background: selectedCategory === cat ? '#444' : '#222', color: '#fff', borderRadius: '6px', whiteSpace: 'nowrap' }}
            >
              {cat}
            </button>
          ))}
        </div>
      </div>

      {/* Product Grid */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '1.5rem', justifyItems: 'center', width: '100%', maxWidth: '1000px', margin: '0 auto' }}>
        <AnimatePresence>
