import React, { useState, useEffect } from 'react';
import { db, storage } from './firebase';
import { collection, addDoc, serverTimestamp, getDocs, deleteDoc, doc, updateDoc } from 'firebase/firestore';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';

const Admin = () => {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [category, setCategory] = useState('');
  const [customCategory, setCustomCategory] = useState('');
  const [image, setImage] = useState(null);
  const [message, setMessage] = useState('');
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [editingProductId, setEditingProductId] = useState(null);
  const [featured, setFeatured] = useState(false);
  const [search, setSearch] = useState('');
  const [showOrders, setShowOrders] = useState(false);

  const fetchProducts = async () => {
    const snapshot = await getDocs(collection(db, 'products'));
    setProducts(snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
  };

  const getUniqueCategories = () => {
    const allCategories = products.map(p => p.category);
    return [...new Set(allCategories.filter(Boolean))];
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    try {
      let imageUrl = '';
      if (image) {
        const imageRef = ref(storage, `products/${image.name}`);
        await uploadBytes(imageRef, image);
        imageUrl = await getDownloadURL(imageRef);
      }

      const selectedCategory = customCategory || category;

      if (editingProductId) {
        const productRef = doc(db, 'products', editingProductId);
        await updateDoc(productRef, {
          name,
          price: parseFloat(price),
          category: selectedCategory,
          ...(image && { image: imageUrl }),
          featured,
        });
        setMessage("Product updated successfully!");
      } else {
        await addDoc(collection(db, "products"), {
          name,
          price: parseFloat(price),
          category: selectedCategory,
          image: imageUrl,
          featured,
          inStock: true,
          createdAt: serverTimestamp()
        });
        setMessage("Product uploaded successfully!");
      }

      setName('');
      setPrice('');
      setCategory('');
      setCustomCategory('');
      setImage(null);
      setFeatured(false);
      setEditingProductId(null);
      fetchProducts();
    } catch (error) {
      console.error("Upload failed:", error);
      alert("Something went wrong.");
    }
  };

  const handleEditProduct = (product) => {
    setEditingProductId(product.id);
    setName(product.name);
    setPrice(product.price);
    setCategory(product.category);
    setFeatured(product.featured || false);
  };

  const handleDeleteProduct = async (productId) => {
    await deleteDoc(doc(db, 'products', productId));
    setProducts(products.filter(p => p.id !== productId));
  };

  const fetchOrders = async () => {
    try {
      const snapshot = await getDocs(collection(db, 'orders'));
      const fetchedOrders = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setOrders(fetchedOrders);
    } catch (error) {
      console.error("Failed to fetch orders:", error);
    }
  };

  const handleDeleteOrder = async (orderId) => {
    try {
      await deleteDoc(doc(db, 'orders', orderId));
      setOrders(prev => prev.filter(order => order.id !== orderId));
    } catch (error) {
      console.error("Failed to delete order:", error);
    }
  };

  const filteredProducts = products.filter(p => p.name.toLowerCase().includes(search.toLowerCase()));

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <div style={{ padding: '2rem', background: '#1a1a1a', minHeight: '100vh', color: '#fff' }}>
      <h2>Admin - {editingProductId ? 'Edit Product' : 'Add Product'}</h2>
      <form onSubmit={handleUpload} style={{ display: 'flex', flexDirection: 'column', maxWidth: '400px' }}>
        <input type="text" placeholder="Product Name" value={name} onChange={(e) => setName(e.target.value)} required style={{ marginBottom: '1rem' }} />
        <input type="number" placeholder="Price" value={price} onChange={(e) => setPrice(e.target.value)} required style={{ marginBottom: '1rem' }} />

        <label style={{ marginBottom: '0.5rem' }}>Select a Category:</label>
        <select value={category} onChange={(e) => setCategory(e.target.value)} style={{ marginBottom: '1rem', padding: '0.5rem' }}>
          <option value="">-- Choose existing category --</option>
          {getUniqueCategories().map((cat, i) => (
            <option key={i} value={cat}>{cat}</option>
          ))}
        </select>
        <input type="text" placeholder="Or enter new category" value={customCategory} onChange={(e) => setCustomCategory(e.target.value)} style={{ marginBottom: '1rem' }} />

        <input type="file" onChange={(e) => setImage(e.target.files[0])} style={{ marginBottom: '1rem' }} />
        <label style={{ marginBottom: '1rem' }}>
          <input type="checkbox" checked={featured} onChange={(e) => setFeatured(e.target.checked)} /> Mark as Hot Item
        </label>
        <button type="submit">{editingProductId ? 'Update Product' : 'Upload Product'}</button>
      </form>
      {message && <p style={{ marginTop: '1rem', color: 'lightgreen' }}>{message}</p>}

      <hr style={{ margin: '2rem 0', borderColor: '#444' }} />

      <h3>Product List</h3>
      <input
        type="text"
        placeholder="Search products..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{ marginBottom: '1rem', padding: '0.5rem', borderRadius: '4px', width: '100%', maxWidth: '400px' }}
      />

      {filteredProducts.map(product => (
        <div key={product.id} style={{ background: '#333', padding: '1rem', marginBottom: '1rem', borderRadius: '8px' }}>
          <p><strong>{product.name}</strong> - ${product.price}</p>
          <p>Category: {product.category}</p>
          <p>{product.featured ? '🔥 Hot Item' : ''}</p>
          <button onClick={() => handleEditProduct(product)} style={{ marginRight: '0.5rem', backgroundColor: '#4CAF50', color: 'white', padding: '0.5rem 1rem', border: 'none', borderRadius: '4px' }}>Edit</button>
          <button onClick={() => handleDeleteProduct(product.id)} style={{ background: 'crimson', color: '#fff', border: 'none', padding: '0.5rem 1rem', borderRadius: '4px' }}>Delete</button>
        </div>
      ))}

      <hr style={{ margin: '2rem 0', borderColor: '#444' }} />
      <button onClick={() => { setShowOrders(!showOrders); if (!showOrders) fetchOrders(); }} style={{ marginBottom: '1rem' }}>
        {showOrders ? 'Hide Orders' : 'Show Orders'}
      </button>

      {showOrders && orders.length > 0 && (
        <div>
          <h3>Customer Orders</h3>
          {orders.map((order, index) => (
            <div key={order.id || index} style={{ marginBottom: '1rem', padding: '1rem', background: '#2a2a2a', borderRadius: '8px' }}>
              <h4 style={{ marginBottom: '0.5rem' }}>JM Distribution</h4>
              <p><strong>Store Name:</strong> {order.storeName || 'N/A'}</p>
              <p><strong>Store Owner:</strong> {order.ownerName || 'N/A'}</p>
              <p><strong>Phone:</strong> {order.phone || 'N/A'}</p>
              <p><strong>Address:</strong> {order.address || 'N/A'}</p>
              <p><strong>Total:</strong> ${order.total ? order.total.toFixed(2) : '0.00'}</p>
              <p><strong>Items Ordered:</strong></p>
              <ul>
                {order.items?.map((item, i) => (
                  <li key={i}>{item.name} × {item.quantity}</li>
                ))}
              </ul>
              <button onClick={() => handleDeleteOrder(order.id)} style={{ marginTop: '0.5rem', background: 'crimson', color: '#fff', border: 'none', padding: '0.5rem 1rem', borderRadius: '4px' }}>Delete Order</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Admin;
