import { useEffect, useState } from 'react';
import { db, storage } from './firebase';
import {
  collection,
  addDoc,
  getDocs,
  deleteDoc,
  doc,
  serverTimestamp
} from 'firebase/firestore';
import {
  ref,
  uploadBytes,
  getDownloadURL
} from 'firebase/storage';

const Admin = () => {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [category, setCategory] = useState('');
  const [image, setImage] = useState(null);
  const [message, setMessage] = useState('');

  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [showOrders, setShowOrders] = useState(false);

  const fetchProducts = async () => {
    const snapshot = await getDocs(collection(db, 'products'));
    const list = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    setProducts(list);
  };

  const fetchOrders = async () => {
    const snapshot = await getDocs(collection(db, 'orders'));
    const orderList = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    setOrders(orderList);
    setShowOrders(true);
  };

  const deleteProduct = async (id) => {
    await deleteDoc(doc(db, 'products', id));
    setProducts(products.filter(p => p.id !== id));
  };

  const deleteOrder = async (id) => {
    await deleteDoc(doc(db, 'orders', id));
    setOrders(orders.filter(order => order.id !== id));
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!image) {
      alert("Please select an image.");
      return;
    }

    try {
      const imageRef = ref(storage, `products/${image.name}`);
      await uploadBytes(imageRef, image);
      const imageUrl = await getDownloadURL(imageRef);

      await addDoc(collection(db, "products"), {
        name,
        price: parseFloat(price),
        category,
        image: imageUrl,
        inStock: true,
        createdAt: serverTimestamp()
      });

      setMessage("Product uploaded successfully!");
      setName('');
      setPrice('');
      setCategory('');
      setImage(null);
      fetchProducts();
    } catch (error) {
      console.error("Upload failed:", error);
      alert("Something went wrong.");
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <div style={{ padding: '2rem', background: '#1a1a1a', minHeight: '100vh', color: '#fff' }}>
      <h2>Admin - Add Product</h2>
      <form onSubmit={handleUpload} style={{ display: 'flex', flexDirection: 'column', maxWidth: '400px' }}>
        <input type="text" placeholder="Product Name" value={name} onChange={(e) => setName(e.target.value)} required style={{ marginBottom: '1rem' }} />
        <input type="number" placeholder="Price" value={price} onChange={(e) => setPrice(e.target.value)} required style={{ marginBottom: '1rem' }} />
        <input type="text" placeholder="Category" value={category} onChange={(e) => setCategory(e.target.value)} required style={{ marginBottom: '1rem' }} />
        <input type="file" onChange={(e) => setImage(e.target.files[0])} required style={{ marginBottom: '1rem' }} />
        <button type="submit">Upload Product</button>
      </form>
      {message && <p style={{ marginTop: '1rem', color: 'lightgreen' }}>{message}</p>}

      <hr style={{ margin: '2rem 0', borderColor: '#444' }} />
      <h3>Current Products</h3>
      {products.length === 0 && <p>No products found.</p>}
      {products.map((p) => (
        <div key={p.id} style={{ background: '#333', padding: '1rem', marginBottom: '1rem', borderRadius: '8px' }}>
          <strong>{p.name}</strong> - ${p.price.toFixed(2)} ({p.category})
          <br />
          <img src={p.image} alt={p.name} style={{ width: '150px', marginTop: '0.5rem' }} />
          <br />
          <button onClick={() => deleteProduct(p.id)} style={{ marginTop: '0.5rem', background: 'crimson', color: '#fff' }}>
            Delete Product
          </button>
        </div>
      ))}

      <hr style={{ margin: '2rem 0', borderColor: '#444' }} />
      <h3>Manage Orders</h3>
      <button onClick={fetchOrders} style={{ marginBottom: '1rem' }}>View Orders</button>

      {showOrders && orders.length === 0 && <p>No orders found.</p>}
      {showOrders && orders.length > 0 && (
        <div>
          {orders.map((order) => (
            <div key={order.id} style={{ marginBottom: '1.5rem', padding: '1rem', background: '#333', borderRadius: '8px' }}>
              <h4>{order.customer.name} â€“ {order.customer.phone}</h4>
              <p><strong>Address:</strong> {order.customer.address}</p>
              <p><strong>Date:</strong> {order.createdAt?.toDate().toLocaleString() || 'â€”'}</p>
              <ul>
                {order.items.map((item, index) => (
                  <li key={index}>{item.name} Ã— {item.quantity} â€” ${item.price.toFixed(2)}</li>
                ))}
              </ul>
              <button onClick={() => deleteOrder(order.id)} style={{ marginTop: '0.5rem', background: 'crimson', color: '#fff' }}>
                Delete Order
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Admin;
