// App.js (Full Features)
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import { db, auth } from './firebase';
import { signInWithEmailAndPassword } from 'firebase/auth';

import {
  collection,
  getDocs,
  addDoc,
  serverTimestamp
} from 'firebase/firestore';
import { onAuthStateChanged, signOut } from 'firebase/auth';
import Admin from './Admin';
import { useNavigate } from 'react-router-dom';


import { motion, AnimatePresence } from 'framer-motion'; // make sure this is imported at the top

const Home = ({ addToCart }) => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');

  useEffect(() => {
    const fetchProducts = async () => {
      const querySnapshot = await getDocs(collection(db, 'products'));
      const items = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setProducts(items);

      const uniqueCategories = [...new Set(items.map(p => p.category))];
      setCategories(uniqueCategories);
    };

    fetchProducts();
  }, []);

  const filteredProducts = selectedCategory
    ? products.filter(p => p.category === selectedCategory)
    : products;

  return (
    <div style={{ padding: '2rem' }}>
      <h1>JM Distribution</h1>

      {/* Scrollable Category Buttons */}
      <div style={{
        margin: '1rem 0',
        overflowX: 'auto',
        whiteSpace: 'nowrap',
        display: 'flex',
        gap: '0.5rem',
        paddingBottom: '0.5rem'
      }}>
        <button
          onClick={() => setSelectedCategory('')}
          style={{
            padding: '0.5rem 1rem',
            background: selectedCategory === '' ? '#444' : '#222',
            color: '#fff',
            border: 'none',
            borderRadius: '6px',
            cursor: 'pointer',
            whiteSpace: 'nowrap'
          }}
        >
          All
        </button>
        {categories.map(cat => (
          <button
            key={cat}
            onClick={() => setSelectedCategory(cat)}
            style={{
              padding: '0.5rem 1rem',
              background: selectedCategory === cat ? '#444' : '#222',
              color: '#fff',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer',
              whiteSpace: 'nowrap'
            }}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* Products Grid with Animation */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1rem' }}>
        <AnimatePresence>
          {filteredProducts.map(p => (
            <motion.div
              key={p.id}
              layout
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0 }}
              transition={{ duration: 0.3 }}
              style={{ background: '#222', padding: '1rem', borderRadius: '8px' }}
            >
              <img src={p.image} alt={p.name} style={{ width: '100%', height: '150px', objectFit: 'cover', marginBottom: '1rem' }} />
              <h3>{p.name}</h3>
              <p>${p.price.toFixed(2)}</p>
              <button onClick={() => addToCart(p)}>Add to Cart</button>
            </motion.div>
          ))}
        </AnimatePresence>
      </div>
    </div>
  );
};




const Cart = ({ cart, removeFromCart }) => {
  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
  return (
    <div style={{ padding: '2rem' }}>
      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          <ul>
            {cart.map(item => (
              <li key={item.id} style={{ marginBottom: '1rem' }}>
                <strong>{item.name}</strong> — ${item.price.toFixed(2)} × {item.quantity}
                <button style={{ marginLeft: '1rem' }} onClick={() => removeFromCart(item.id)}>Remove</button>
              </li>
            ))}
          </ul>
          <h3>Total: ${total.toFixed(2)}</h3>
          <Link to="/checkout">
            <button style={{ marginTop: '1rem' }}>Proceed to Checkout</button>
          </Link>
        </>
      )}
    </div>
  );
};

const Checkout = ({ cart, clearCart }) => {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [address, setAddress] = useState('');
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (cart.length === 0) {
      alert("Cart is empty.");
      return;
    }

    const order = {
      customer: { name, phone, address },
      items: cart,
      createdAt: serverTimestamp()
    };

    try {
      await addDoc(collection(db, "orders"), order);
      setSubmitted(true);
      clearCart();
    } catch (error) {
      console.error("Error submitting order:", error);
      alert("There was a problem submitting the order.");
    }
  };

  if (submitted) {
    return (
      <div style={{ padding: '2rem' }}>
        <h2>Order Placed!</h2>
        <p>Thank you for your order. We'll contact you shortly.</p>
      </div>
    );
  }

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Checkout</h2>
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', maxWidth: '400px' }}>
        <input placeholder="Full Name" value={name} onChange={e => setName(e.target.value)} required style={{ marginBottom: '1rem' }} />
        <input placeholder="Phone Number" value={phone} onChange={e => setPhone(e.target.value)} required style={{ marginBottom: '1rem' }} />
        <textarea placeholder="Shipping Address" value={address} onChange={e => setAddress(e.target.value)} required style={{ marginBottom: '1rem' }} />
        <button type="submit">Place Order</button>
      </form>
    </div>
  );
};

const ProtectedRoute = ({ user, children }) => {
  return user ? children : <Navigate to="/" />;
};


