// App.js (Full Features)
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import { db, auth } from './firebase';
import { signInWithEmailAndPassword } from 'firebase/auth';
import {
  collection,
  getDocs,
  addDoc,
  serverTimestamp
} from 'firebase/firestore';
import { onAuthStateChanged, signOut } from 'firebase/auth';
import Admin from './Admin';
import { useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { useEffect, useState, useRef } from 'react';
import ProtectedRoute from './ProtectedRoute';
import CartIcon from './CartIcon';
import { FaTrash } from 'react-icons/fa';

const Home = ({ addToCart }) => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [quantities, setQuantities] = useState({});
  const scrollRef = useRef();
  const cartIconRef = useRef();

  useEffect(() => {
    const fetchProducts = async () => {
      const querySnapshot = await getDocs(collection(db, 'products'));
      const items = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      const sorted = items.sort((a, b) => (a.createdAt?.seconds || 0) - (b.createdAt?.seconds || 0));
      setProducts(sorted);
      const uniqueCategories = [...new Set(sorted.map(p => p.category))];
      setCategories(uniqueCategories);

      const initialQuantities = {};
      sorted.forEach(item => {
        initialQuantities[item.id] = 1;
      });
      setQuantities(initialQuantities);
    };

    fetchProducts();
  }, []);

  const handleQuantityChange = (productId, value) => {
    const qty = Math.max(1, parseInt(value) || 1);
    setQuantities(prev => ({ ...prev, [productId]: qty }));
  };

  const flyToCart = (imgRef) => {
    const cartIcon = document.getElementById('cart-icon');
    if (!imgRef?.current || !cartIcon) return;

    const img = imgRef.current;
    const clone = img.cloneNode(true);
    const imgRect = img.getBoundingClientRect();
    const cartRect = cartIcon.getBoundingClientRect();

    clone.style.position = 'fixed';
    clone.style.left = `${imgRect.left}px`;
    clone.style.top = `${imgRect.top}px`;
    clone.style.width = `${img.offsetWidth}px`;
    clone.style.height = `${img.offsetHeight}px`;
    clone.style.zIndex = '1000';
    clone.style.transition = 'all 0.8s ease-in-out';
    document.body.appendChild(clone);

    requestAnimationFrame(() => {
      clone.style.left = `${cartRect.left}px`;
      clone.style.top = `${cartRect.top}px`;
      clone.style.width = '20px';
      clone.style.height = '20px';
      clone.style.opacity = '0.2';
    });

    setTimeout(() => {
      document.body.removeChild(clone);
    }, 800);
  };

  const buttonStyle = (active) => ({
    padding: '0.5rem 1.2rem',
    background: active ? '#ff4081' : 'transparent',
    color: active ? '#fff' : '#ff4081',
    border: '2px solid #ff4081',
    borderRadius: '9999px',
    fontWeight: 'bold',
    cursor: 'pointer',
    transition: 'all 0.2s ease-in-out',
    whiteSpace: 'nowrap'
  });

  const displayProducts = selectedCategory
    ? products.filter(p => p.category === selectedCategory)
    : products;

  return (
    <div style={{ padding: '2rem', background: '#1a1a1a', minHeight: '100vh', color: '#fff' }}>
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', position: 'sticky', top: 0, background: '#1a1a1a', zIndex: 10, paddingBottom: '1rem' }}>
        <h1 style={{ textAlign: 'center', marginBottom: '1rem' }}>JM Distribution</h1>
        <div ref={scrollRef} style={{ display: 'flex', overflowX: 'auto', gap: '0.75rem', marginBottom: '1rem', paddingBottom: '0.5rem' }}>
          <button onClick={() => setSelectedCategory('')} style={buttonStyle(selectedCategory === '')}>All</button>
          {categories.map(cat => (
            <button key={cat} onClick={() => setSelectedCategory(cat)} style={buttonStyle(selectedCategory === cat)}>{cat}</button>
          ))}
        </div>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '1rem', justifyItems: 'center', width: '100%', maxWidth: '1200px', margin: '0 auto' }}>
        <AnimatePresence>
          {displayProducts.map(p => {
            const imgRef = useRef();
            return (
              <motion.div key={p.id} layout initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} exit={{ opacity: 0 }} transition={{ duration: 0.3 }} style={{ background: '#222', padding: '1rem', borderRadius: '10px', textAlign: 'center', width: '100%', maxWidth: '250px', display: 'flex', flexDirection: 'column', justifyContent: 'space-between', minHeight: '350px' }}>
                {p.featured && (
                  <span style={{ position: 'absolute', top: '10px', right: '10px', background: '#ff4081', color: '#fff', padding: '2px 6px', fontSize: '0.75rem', borderRadius: '4px' }}>Hot Item</span>
                )}
                {!p.inStock && (
                  <span style={{ position: 'absolute', top: '10px', left: '10px', background: 'gray', color: '#fff', padding: '2px 6px', fontSize: '0.75rem', borderRadius: '4px' }}>Sold Out</span>
                )}
                {p.image && <img ref={imgRef} src={p.image} alt={p.name} style={{ width: '100%', height: '150px', objectFit: 'contain', marginBottom: '1rem' }} />}
                <h3 style={{ marginBottom: '0.25rem' }}>{p.name}</h3>
                <p style={{ marginBottom: '0.5rem' }}>${p.price.toFixed(2)}</p>
                <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '0.5rem', gap: '0.5rem' }}>
                  <button onClick={() => handleQuantityChange(p.id, (quantities[p.id] || 1) - 1)} style={{ width: '30px', height: '30px', borderRadius: '50%', border: 'none', background: '#ff4081', color: '#fff', fontWeight: 'bold', fontSize: '1rem', cursor: 'pointer' }}>-</button>
                  <input type="number" min="1" value={quantities[p.id] || 1} onChange={(e) => handleQuantityChange(p.id, e.target.value)} style={{ width: '40px', textAlign: 'center', borderRadius: '999px', border: '1px solid #ccc', padding: '0.25rem 0.5rem' }} />
                  <button onClick={() => handleQuantityChange(p.id, (quantities[p.id] || 1) + 1)} style={{ width: '30px', height: '30px', borderRadius: '50%', border: 'none', background: '#ff4081', color: '#fff', fontWeight: 'bold', fontSize: '1rem', cursor: 'pointer' }}>+</button>
                </div>
                <button onClick={() => { addToCart({ ...p, quantity: quantities[p.id] || 1 }); flyToCart(imgRef); }} disabled={!p.inStock} style={{ marginTop: '0.5rem', background: p.inStock ? '#ff4081' : '#555', color: '#fff', border: 'none', borderRadius: '9999px', padding: '0.5rem 1rem', fontWeight: 'bold', cursor: p.inStock ? 'pointer' : 'not-allowed', opacity: p.inStock ? 1 : 0.6 }}>
                  {p.inStock ? 'Add to Cart' : 'Out of Stock'}
                </button>
              </motion.div>
            );
          })}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default Home;
