import React, { useEffect, useState, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { collection, getDocs } from 'firebase/firestore';
import { db } from './firebase';

const Home = ({ addToCart }) => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const scrollRef = useRef();

  useEffect(() => {
    const fetchProducts = async () => {
      const querySnapshot = await getDocs(collection(db, 'products'));
      const items = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setProducts(items);
      const uniqueCategories = [...new Set(items.map(p => p.category))];
      setCategories(uniqueCategories);
    };

    fetchProducts();
  }, []);

  const filteredProducts = selectedCategory
    ? products.filter(p => p.category === selectedCategory)
    : products;

  return (
    <div style={{ padding: '2rem', background: '#1a1a1a', minHeight: '100vh', color: '#fff', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <h1 style={{ textAlign: 'center' }}>JM Distribution</h1>

      {/* Scrollable, Swipeable Category Buttons */}
      <div
        ref={scrollRef}
        style={{
          margin: '1rem 0',
          overflowX: 'auto',
          display: 'flex',
          gap: '0.5rem',
          paddingBottom: '0.5rem',
          scrollbarWidth: 'none',
          msOverflowStyle: 'none'
        }}
        onTouchStart={(e) => {
          scrollRef.current.touchStartX = e.touches[0].clientX;
        }}
        onTouchMove={(e) => {
          const deltaX = scrollRef.current.touchStartX - e.touches[0].clientX;
          scrollRef.current.scrollLeft += deltaX;
          scrollRef.current.touchStartX = e.touches[0].clientX;
        }}
      >
        <button
          onClick={() => setSelectedCategory('')}
          style={{
            padding: '0.5rem 1rem',
            background: selectedCategory === '' ? '#444' : '#222',
            color: '#fff',
            border: 'none',
            borderRadius: '6px',
            cursor: 'pointer',
            whiteSpace: 'nowrap'
          }}
        >
          All
        </button>
        {categories.map(cat => (
          <button
            key={cat}
            onClick={() => setSelectedCategory(cat)}
            style={{
              padding: '0.5rem 1rem',
              background: selectedCategory === cat ? '#444' : '#222',
              color: '#fff',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer',
              whiteSpace: 'nowrap'
            }}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* Product Grid with Featured Tags + Animation */}
      <div style={{ width: '100%', maxWidth: '1000px', display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1rem', justifyContent: 'center' }}>
        <AnimatePresence>
          {filteredProducts.map(p => (
            <motion.div
              key={p.id}
              layout
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0 }}
              transition={{ duration: 0.3 }}
              style={{ background: '#222', padding: '1rem', borderRadius: '8px', position: 'relative', textAlign: 'center' }}
            >
              {p.featured && (
                <span style={{
                  position: 'absolute',
                  top: '10px',
                  left: '10px',
                  background: '#ff4081',
                  color: '#fff',
                  padding: '2px 6px',
                  fontSize: '0.75rem',
                  borderRadius: '4px'
                }}>
                  FEATURED
                </span>
              )}
              <img src={p.image} alt={p.name} style={{ width: '100%', height: '150px', objectFit: 'cover', marginBottom: '1rem' }} />
              <h3>{p.name}</h3>
              <p>${p.price.toFixed(2)}</p>
              <button onClick={() => addToCart(p)}>Add to Cart</button>
            </motion.div>
          ))}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default Home;
