import React, { useEffect, useState } from 'react';
import {
  collection,
  addDoc,
  getDocs,
  deleteDoc,
  doc,
  updateDoc,
  serverTimestamp
} from 'firebase/firestore';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import { db, storage } from './firebase';

const Admin = () => {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [category, setCategory] = useState('');
  const [image, setImage] = useState(null);
  const [featured, setFeatured] = useState(false);
  const [message, setMessage] = useState('');
  const [products, setProducts] = useState([]);
  const [editingProductId, setEditingProductId] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const snapshot = await getDocs(collection(db, 'products'));
    const items = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    setProducts(items);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!image) return alert("Please select an image");

    try {
      const imageRef = ref(storage, `products/${image.name}`);
      await uploadBytes(imageRef, image);
      const imageUrl = await getDownloadURL(imageRef);

      await addDoc(collection(db, 'products'), {
        name,
        price: parseFloat(price),
        category,
        featured,
        image: imageUrl,
        createdAt: serverTimestamp()
      });

      setMessage('Product uploaded!');
      resetForm();
      fetchProducts();
    } catch (err) {
      console.error(err);
      alert('Upload failed.');
    }
  };

  const saveEdit = async (e) => {
    e.preventDefault();
    try {
      await updateDoc(doc(db, 'products', editingProductId), {
        name,
        price: parseFloat(price),
        category,
        featured
      });
      setMessage('Product updated!');
      resetForm();
      fetchProducts();
    } catch (err) {
      console.error(err);
      alert('Update failed.');
    }
  };

  const deleteProduct = async (id) => {
    await deleteDoc(doc(db, 'products', id));
    fetchProducts();
  };

  const startEdit = (product) => {
    setEditingProductId(product.id);
    setName(product.name);
    setPrice(product.price);
    setCategory(product.category);
    setFeatured(!!product.featured);
  };

  const resetForm = () => {
    setName('');
    setPrice('');
    setCategory('');
    setImage(null);
    setFeatured(false);
    setEditingProductId(null);
  };

  return (
    <div style={{ padding: '2rem', background: '#1a1a1a', minHeight: '100vh', color: '#fff' }}>
      <h2>Admin Dashboard</h2>
      <form onSubmit={editingProductId ? saveEdit : handleUpload} style={{ display: 'flex', flexDirection: 'column', maxWidth: '400px' }}>
        <input type="text" placeholder="Product Name" value={name} onChange={(e) => setName(e.target.value)} required style={{ marginBottom: '1rem' }} />
        <input type="number" placeholder="Price" value={price} onChange={(e) => setPrice(e.target.value)} required style={{ marginBottom: '1rem' }} />
        <input type="text" placeholder="Category" value={category} onChange={(e) => setCategory(e.target.value)} required style={{ marginBottom: '1rem' }} />
        <input type="file" onChange={(e) => setImage(e.target.files[0])} style={{ marginBottom: '1rem' }} />

        <label style={{ color: '#fff', marginBottom: '1rem' }}>
          <input
            type="checkbox"
            checked={featured}
            onChange={(e) => setFeatured(e.target.checked)}
            style={{ marginRight: '0.5rem' }}
          />
          Mark as Featured
        </label>

        <button type="submit">{editingProductId ? 'Save Changes' : 'Upload Product'}</button>
      </form>

      {message && <p style={{ color: 'lightgreen', marginTop: '1rem' }}>{message}</p>}

      <hr style={{ margin: '2rem 0', borderColor: '#444' }} />

      <h3>All Products</h3>
      {products.map(product => (
        <div key={product.id} style={{ background: '#333', padding: '1rem', borderRadius: '6px', marginBottom: '1rem' }}>
          <strong>{product.name}</strong> - ${product.price} [{product.category}]{' '}
          {product.featured && <span style={{ color: '#ff4081', marginLeft: '0.5rem' }}>(Featured)</span>}
          <div style={{ marginTop: '0.5rem' }}>
            <button onClick={() => startEdit(product)} style={{ marginRight: '1rem' }}>Edit</button>
            <button onClick={() => deleteProduct(product.id)}>Delete</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Admin;
